	if(tex!=''){
		if(!isNaN(tex)){ //表示是全数字
			//if(!(/^1[3|4|5|8][0-9]\d{4,9}$/.test(tex))){ //如果不是手机,则表示是QQ号
				if(tex.length>12){ 
