<script type="text/javascript">
$('.i-info .chongzhi').click(function(){
		$('#cboxOverlay').css('opacity','0.8');
		$('.chongzhi-box,#cboxOverlay').fadeIn();
	})
	$('.chongzhi-box h3 span').click(function(){
		$('.chongzhi-box,#cboxOverlay').fadeOut();
	})
	$('.chongzhi-box .codes img').click(function(){
		$(this).attr('src','createParityImage.jsp?'+Math.random())
	})
	function checkCardForm(){
		var couponName = $('#card-id').val();
		var couponPw = $('#card-pwd').val();
		var amount = $('#card-money').val();
		if(couponName==''||couponName==null){
			$('#card-id').css('border','1px solid rgb(253, 173, 173')
			return false;
		}else{
			$('#card-id').css('border','1px solid #ccc')
		}
		if(couponPw==''||couponPw==null){
			$('#card-pwd').css('border','1px solid rgb(253, 173, 173')
			return false;
		}else{
			$('#card-pwd').css('border','1px solid #ccc')
		}
		if(amount==''||amount==null){
			$('#card-money').css('border','1px solid rgb(253, 173, 173')
			return false;
		}else{
			$('#card-money').css('border','1px solid #ccc')
		}
		if($('#codes').val()==''||$('#codes').val()==null){
			$('#codes').css('border','1px solid rgb(253, 173, 173')
			return false;
		}else{
			$('#codes').css('border','1px solid #ccc')
		}
		
		var dataStr="couponName="+couponName+"&couponPw="+couponPw+"&amount="+amount;
		$.ajax({
			type:"POST",
			url:"couponInfoAction!payToDinDinCoin.go?"+dataStr,			
			dataType:"json",
			success:function(data){			
			 location.reload();	
			 alert(data);
	          //$('.chongzhi-box,#cboxOverlay').fadeOut(); 
	        },        
	        error: function(){	         
	          location.reload();
	          alert("充值失败!");
	       	} 
		});
		$('.chongzhi-box,#cboxOverlay').hide();
		$('.cboxOverlay1').fadeIn();
	}


